.wiki-lemma .lemmaWgt-secondsKnow,
.lemmaWgt-promotion-slide .promotion_viewport,
.lemmaWgt-promotion-vbaike,
.professional-con,
.pc-header-new .topbar,
.zhidao-diversion,
.configModuleDown,
.navbar-wrapper,
#lemmaWgt-secondsKnow,
#tashuo_bottom,
#tashuo_right,
#side-auth,
#side-share,
#hotspotmining_s,
#side_box_unionAd {
    display: none!important;
}
/****************** 全局设置 ******************/



body {
    background: #333;
}
.body-wrapper .content-wrapper .content {
    font-family: Microsoft YaHei, WenQuanYi Micro Hei, sans-serif!important;
}
/****************** 导航栏 ******************/

.header-wrapper .header .layout {
    width: 100%;
}
.lemmaWgt-searchHeader {
    border: none;
}
.lemmaWgt-searchHeader,
.header-wrapper .header {
    background-color: #4F9CEE;
}
.pc-header-new .wgt-searchbar-main {
    width: 930px;
    margin: auto;
}
/* 百度图标 */

.cmn-icons_logo-du {
    color: rgba(254, 254, 254, 1);
}
.cmn-icons_logo-bai,
.cmn-icons_logo-baike {
    color: rgba(255, 255, 255, 0.54);
}
/* 下拉选项 */

.wgt-searchbar-new.wgt-searchbar .search .form .suggestion li {
    padding: 0 8px;
    height: 30px;
    line-height: 30px;
    font-size: 16px;
    cursor: pointer;
}
/*  搜索栏 */

.wgt-searchbar-new.wgt-searchbar .search .form input {
    height: 30px;
    line-height: 1.5;
    font-size: 16px;
    border: none;
    border-radius: 5px;
    background: hsla(0, 0%, 100%, .2);
    color: #fff;
    transition: background .3s;
}
.wgt-searchbar-new.wgt-searchbar .search .form input:focus {
    box-shadow: 0 0 5px rgba(255, 255, 255, .5);
    background: hsla(0, 0%, 100%, .9);
    color: #222;
    transition: background .3s, box-shadow .5s;
}
#searchForm > button {
    height: 40px;
    line-height: 1.5;
    border-radius: 5px;
    margin-left: 5px;
    border: none!important;
    box-shadow: 0 0 0 transparent;
    color: #fff!important;
    background: hsla(0, 50%, 100%, .2)!important;
}
#searchForm > button:hover {
    background: hsla(0, 50%, 100%, .9)!important;
    box-shadow: 0 0 10px rgba(255, 255, 255, .5);
    color: #222!important;
    transition: box-shadow .5s, background .5s;
}
.wgt-navbar .navbar-bg .navbar-bg-top {
    height: 43px;
    border-top: none;
    border-bottom: none;
    background: #7c9b9e;
}
.wgt-searchbar-new.wgt-searchbar .logo-container {
    padding: 12px 0 15px;
}
.tool-buttons {
    margin-top: 30px;
}
.tool-buttons .button {
    color: #fff;
    font-size: 16px;
}
.lemmaWgt-searchHeader .user-info {
    margin-top: 13px;
}
.lemmaWgt-searchHeader .user-info a.user-link {
    text-decoration: none;
    font-size: 16px;
    color: #fff;
}
.wgt-searchbar-new.wgt-searchbar-large .search {
    padding: 15px 0px;
}
.wgt-searchbar-new.wgt-searchbar-large .logo-container {
    padding: 10px 0px;
}
/****************** 正文 ******************/


.body-wrapper .content-wrapper {
    margin: 20px 0 30px;
}
.main-content table.table-view,
.body-wrapper .para {
    font-size: 14px;
    line-height: 1.8;
}



.page-background{
    background:#f5f5f5!important;
}

.content {
    margin-bottom: 30px;
}


.after-science-page-background{
    margin-top:0!important;
}

.polysemant-list-normal .polysemantList-header{
    border-bottom:1px solid #fff!important;
}
.before-content {
    width: 1140px;
    margin: 10px auto 0px;
}
.before-content .pinzhuanWrap {
    padding: 5px 0;
}
.polysemant-list-normal .polysemantList-wrapper {
    background: white;
}
.lemma-picture,
.side-box,
.zhidao-diversion,
.lemmaWgt-sideRecommend,
.wiki-lemma #authEdit,
.wiki-lemma #authResource,
.wiki-lemma .summary-pic {
    border: none;
    box-shadow: 0 0 0 transparent;
}
.lemmaWgt-lemmaCatalog .lemma-catalog .catalog-list ol li.level1 .text,
.lemmaWgt-lemmaCatalog .lemma-catalog .catalog-list,
.lemmaWgt-lemmaCatalog .lemma-catalog {
    background: #fcfcfc;
    border: none;
}
.lemmaWgt-lemmaCatalog .lemma-catalog {
    border-radius: 5px;
}
.lemmaWgt-lemmaCatalog .lemma-catalog .catalog-list ol li.level2 .text {
    font-size: 14px;
}
.polysemant-list .polysemantList-header .polysemantList-header-title .toggle.expand,
.polysemant-list .polysemantList-header .polysemantList-header-title .toggle.collapse,
.polysemant-list .polysemantList-header .polysemantList-addButton {
    width: auto;
}



/* 底栏排版 */

.wgt-footer-main {
    width: 1140px;
    margin: 10px auto 0px;
}
/************************* 加入动画效果 ****************************/

.body-wrapper {
    animation-name: left_logoR;
    -webkit-animation-duration: .5s;
    -moz-animation-duration: .5s;
    animation-duration: .5s;
    -webkit-animation-timing-function: ease;
    -moz-animation-timing-function: ease;
    animation-timing-function: ease;
}
@-webkit-keyframes left_logoR {
    0% {
        -webkit-transform: translateY(64px);
        -moz-transform: translateY(64px);
        transform: translateY(64px);
        opacity: 0;
    }
    50% {
        opacity: 0;
    }
    100% {
        opacity: 1;
    }
}

/* 排版 */

#body{
    margin-bottom:50px;
}

a {
    text-decoration: none!important;
}
.search-box-new{
    height:55px;
}


.qb-section{
    width: 1000px;
    margin: auto;
}
.has-menu .search-box-new .search-cont {
    width: 730px!important;
}
.wgt-header-title .wgt-header-title-content {
    
    width: 1000px;
   
}
.wgt-header-title-btn{
    float:right;
    margin-top:10px;
}

#qb-content {
    padding: 0;
    width: 100%;
    border-radius: 5px;
    box-sizing: border-box;
}
#qb-content>div {
    padding: 0;
    border: none;
    margin: 10px auto;
}
#qb-content>div:first-child {
    margin-top: 0;
}
#qb-content>div:last-child {
    margin-bottom: 0;
}
/* 问答 */

.wgt-ask {
    padding: 10px 20px!important;
}
/* 最好答案 */
div.wgt-best .hd{
    margin-top:0!important;
}
.wgt-best .hd {
    padding: 7px 10px;
}
.wgt-best .bd {
    padding: 0 20px;
}
.wgt-best .wgt-replyer-special-bg {
    border: none;
}
/* 其它回答 */

.wgt-answers {
    padding-bottom: 20px !important;
}
.wgt-answers .hd h2 {
    padding: 10px 20px;
}
.wgt-answers .bd-wrap {
    border: none;
}
.wgt-answers .bd {
    border-bottom: 1px dotted #e9e9e9;
    padding: 24px 20px 0px;
}
.wgt-answers #show-fold-container,
.wgt-answers #show-hide-container,
.wgt-answers .show-answer-dispute {
    height: 40px;
    line-height: 40px;
    margin: 15px 420px 5px;
    border-radius: 60px;
}
.wgt-answers #show-answer-fold,
.wgt-answers #show-answer-hide,
.wgt-answers .show-hide-dispute {
    cursor: pointer;
    font-size: 16px;
}
.wgt-answers #show-answer-fold.hover,
.wgt-answers #show-answer-hide.hover,
.wgt-answers .show-hide-dispute.hover {
    border-radius: 60px;
    text-decoration: none;
}
/* 列表 */

.wgt-related,
.wgt-topic,
.wgt-related .related-list,
.wgt-push {
    padding: 0!important;
}
.wgt-silder-push u1,
.wgt-related .related-list {
    margin: 0;
}
.wgt-topic h2,
.wgt-related h2,
.wgt-push h2 {
    padding: 10px 20px;
}
.wgt-topic ul,
.wgt-related ul,
.wgt-push ul,
.wgt-topic .topic-list,
.wgt-push .push-ul {
    margin: 0px;
    padding: 0px;
}
.wgt-topic ul li,
.wgt-related ul li,
.wgt-push ul li {
    padding: 5px 20px;
}
.wgt-topic ul li:hover a,
.wgt-related ul li:hover span,
.wgt-push ul li:hover span,
.wgt-push ul li:hover a {
    font-weight: bold;
}
/* 调节 */

#qb-content .wgt-push,
#qb-content .push-ul,
#qb-content .wgt-related,
#qb-content .related-list,
#qb-content .wgt-topic,
#qb-content .topic-list {
    margin-top: 0;
    padding-top: 0;
}
#qb-content .wgt-push,
#qb-content .push-ul {
    max-height: 500px;
}

.wgt-best-operator{
    padding-bottom:10px;
}


.f-aid,
a.f-aid {
    color: #333;
}
body .pager a:hover {
    color: #fff;
    border: solid 1px #43cc69;
}


/* 修复排版错位 */
.accuse-area .wgt-accuse {
   
    line-height: 1;
    
}

#answer-bar {
    line-height: 30px;
    
}

.wgt-ask .ask-info .wgt-accuse {
   
    top: 9px;
    
}
.wgt-replyer-all {
    margin-left: 20px;
}

/* 背景和颜色 */

body {
   
    background-color: #f1f2f3;
    background-size: cover;
    /*     filter:blur(5px); */
}
#qb-content>div {
    background: #fff;
}
/* 问答 */

.wgt-ask:hover {
    background: #f5f5f5;
}
/* 最佳答案 */

.wgt-best .hd {
    background: #27ae61;
}
.wgt-best .hd span {
    color: #fff;
}
.wgt-best .wgt-replyer-special-bg {
    background: rgba(218, 224, 228, 0.21);
}
div.wgt-best .answer-title{
    font-size:20px;
    width:100px;
}
/* 其它回答 */

.wgt-answers .hd h2 {
    background: #2aa198;
}
.wgt-answers .hd h2 {
    color: #fff;
}
.wgt-answers .bd:hover {
    background: #f8f8f8;
}
.wgt-answers #show-fold-container,
.wgt-answers #show-hide-container,
.wgt-answers .show-answer-dispute {
    background: #46b39e;
    color: #fff;
}
.wgt-answers #show-answer-fold,
.wgt-answers #show-answer-hide,
.wgt-answers .show-hide-dispute {
    color: #fff;
}
.wgt-answers #show-answer-fold.hover,
.wgt-answers #show-answer-hide.hover,
.wgt-answers .show-hide-dispute.hover {
    background: #1bbc9b;
    color: #fff;
}
/* 列表 */

.wgt-topic h2,
.wgt-related h2,
.wgt-push h2 {
    background: #95a5a5;
}
.wgt-topic h2,
.wgt-related h2,
.wgt-push h2,
.wgt-topic h2 em {
    color: #fff;
}
.wgt-topic li:nth-child(even),
.wgt-related ul li:nth-child(even),
.wgt-push ul li:nth-child(even) {
    background: #f9fafb;
}
.wgt-topic ul li:hover,
.wgt-related ul li:hover,
.wgt-push ul li:hover {
    background: #f5f5f5;
}
.wgt-topic .topic-list li:hover a,
.wgt-push .push-ul li:hover a,
.wgt-topic ul li:hover a,
.wgt-related ul li:hover span,
.wgt-push ul li:hover span,
.wgt-push ul li:hover a {
    color: #333;
}
.wgt-topic ul li span,
.wgt-related ul li span,
.wgt-push ul li span {
    color: #333;
}
/* 调节 */

.f-aid,
a.f-aid {
    color: #333;
}
body .pager b {
    background: #2aa198;
}
body .pager a:hover {
    color: #fff;
    background: #34bab0;
}

/* 屏蔽内容 */
div[class*="-ads-"],
.magzine-list,
.footer,
.s-tools,
.new-icon,
.warning-warpper,
.wgt-common-nav .main-menu li:last-child,
.top-search-box ul.channel,
.wgt-feeds-video,
#aside,
#msg-arrow,
#exp-msgbox,
#wgt-left-promo,
#wgt-like {
    display: none!important;
}
/* 调整排版 */
.body-wrapper .content-container {
    animation-name: left_logoR;
    -webkit-animation-duration: .2s;
    -webkit-animation-timing-function: ease;
}
.breadcrumb,
#bread-wrap,
.body-wrapper{
    background-color:#f1f1f1;
}
.body-wrapper .content-container .main-content{
    background-color:#fefefe;
}

.top-search-box .s-nav {
    height: 20px;
}
.userbar {
    top: 30px;
}



.header,
.nav .nav-wrap,
.breadcrumb .bread-wrap,
.content-container {
    width: 1030px;
    margin:0 auto;
}


#main-content {
    padding: 0;
    width: inherit;
    margin:10px auto;
    border-radius: 3px;
    box-shadow: 0 1px 10px rgba(0, 0, 0, 0.12);
    -webkit-box-shadow: 0 1px 10px rgba(0, 0, 0, 0.12);
    -moz-box-shadow:0  1px 10px rgba(0, 0, 0, 0.12);
}

#exp-article>div[class^="exp-"],
#exp-article>div[class^="wgt-"],
