.search-aside-adWrap,
.tiger-shifen-dialog,
.tiger-lossUser-dialog,
.search-aside-newadWrap,
div[class^="ec_src"],
#wkgg,
#xs-task,
#user-my-class,
#share-tips,
#banurl,
#nav-gaokao,
#guess-like-doc,
